package Loops;

public class Testloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1;
		
		for(int i=1;i<=3;i++)

		{
			System.out.println(a + "X" +i+"="+(a*i));
		}
		

		int r=2;
		
		for(int w=1;w<=3;w++)

		{
			System.out.println(r + "X" +w+"="+(r*w));
		}

		int t=3;
		
		for(int u=1;u<=3;u++)

		{
			System.out.println(t + "X" +u+"="+(t*u));
		}
		
		
	}

}
